Action.ExecScriptExpression:
using System.Net;
using System.Net.Sockets;

try {
  var settings = TriggernometryHelpers.GetTableVariable(true, "LSSettings");
  var cmd = TriggernometryHelpers.EvaluateStringExpression("${_event}");
  TriggernometryHelpers.Log(3, "[LiveSplit] Command started: " + cmd);

  var ip = settings.Peek(settings.SeekColumn("Server IP"),2).ToString();
  var port = Int32.Parse(settings.Peek(settings.SeekColumn("Server Port"),2).ToString());
  IPEndPoint ipe = new IPEndPoint(IPAddress.Parse(ip), port);
  Socket ws = new Socket(AddressFamily.InterNetwork, SocketType.Stream, ProtocolType.Tcp);
  TriggernometryHelpers.Log(4, "[LiveSplit] Connecting to WebSocket @ " + ip + ":" + port);
  ws.Connect(ipe);
  TriggernometryHelpers.Log(4, "[LiveSplit] Successfully connected to " + ip + ":" + port + ". Sending command: " + cmd);
  
  byte[] msg = System.Text.Encoding.ASCII.GetBytes(cmd + "\r\n");
  ws.Send(msg);
  TriggernometryHelpers.Log(4, "[LiveSplit] Command \"" + cmd + "\" sent. Closing Connection @ " + ip + ":" + port);
  ws.Shutdown(SocketShutdown.Both);
  ws.Close();
  TriggernometryHelpers.Log(3, "[LiveSplit] Command \"" + cmd + "\" finished without errors.");
} catch (SocketException ex) {
  System.Media.SystemSounds.Hand.Play();
  System.Windows.Forms.MessageBox.Show("Unable to connect to server. Please make sure it is running by right clicking LiveSplit and going to Control -> Start Server.\n\nIf the issue still persists, make sure the IP and Port are configured correctly and check the logs.", "LiveSplit Connection Error");
  TriggernometryHelpers.Log(1, "[LiveSplit] SocketException: " + ex.Message);
} catch (Exception ex) {
  System.Media.SystemSounds.Hand.Play();
  System.Windows.Forms.MessageBox.Show(ex.Message, "LiveSplit WS Exception");
  TriggernometryHelpers.Log(1, "[LiveSplit] Script Exception: " + ex.Message);
}
